$('#davey').hover(function(){

       $(this).css('font-weight','bold');
        $('#daveypic').css('border-radius','50%');

    }, function(){

        $(this).css({
            'color':'black', 
            'font-weight':'normal'});
        $('#daveypic').css('border-radius','5px');

});

$('#daveypic').hover(function(){

       $(this).css('border-radius','50%');
        $('#davey').css('font-weight','bold');

    }, function(){

        $(this).css('border-radius','5px');
        $('#davey').css({
            'color':'black', 
            'font-weight':'normal'});

});

